Component({
  props: {
    maskZindex: '',
    // product: 产品弹窗蒙层；market：营销弹窗蒙层；
    type: 'product',
    onMaskTap: function onMaskTap() {},
    show: true
  },
  methods: {
    onMaskClick: function onMaskClick() {
      var onMaskTap = this.props.onMaskTap;

      if (onMaskTap !== '' && typeof onMaskTap === 'function') {
        onMaskTap();
      }
    }
  }
});