Component({
  data: {
    show: true
  },
  props: {
    className: '',
    time: 5000,
    onClose: function onClose() {}
  },
  didMount: function didMount() {
    var _this = this;

    var time = this.props.time;
    this._timer = setTimeout(function () {
      _this.setData({
        show: false
      });
    }, time);
  },
  didUnmount: function didUnmount() {
    clearTimeout(this._timer);
  },
  methods: {
    onClose: function onClose() {
      this.setData({
        show: false
      });
      this.props.onClose();
    }
  }
});